import {
    FETCH_LIST,
} from "./types";

import CloudService from "../services/cloud.service";
import { NotificationManager } from 'react-notifications';

export const fetchList = () => (dispatch) => {
    return CloudService.getVmList().then(
        (data) => {

            dispatch({
                type: FETCH_LIST,
                payload: { vmList: data.data },
            });

            NotificationManager.success('Virtual Machine Listed Fetched', "Success",3000);

            return Promise.resolve();
        },
        (error) => {

            const message =
                (error.response &&
                    error.response.data) ||
                error.toString();

            NotificationManager.error(message, 'Click me!', 5000);

            return Promise.reject();
        }
    );
};

export const activateMachine = ({instanceName, zone}) => (dispatch) => {

        return CloudService.activateMachine({instanceName, zone}).then(
            (data) => {

                dispatch({
                    type: FETCH_LIST,
                    payload: { vmList: data.data },
                });

                NotificationManager.success(instanceName + 'Virtual Machine Has Been Activated', "Success", 3000);

                return Promise.resolve();
            },
            (error) => {
                console.log(error)
                const message =
                    (error.response &&
                        error.response.data) ||
                    error.toString();

                NotificationManager.error(message, 'Error!', 5000);

                return Promise.reject();
            }

        );
};

export const deactivateMachine = ({instanceName, zone}) => (dispatch) => {

    return CloudService.deactivateMachine({instanceName, zone}).then(
        (data) => {
            dispatch({
                type: FETCH_LIST,
                payload: { vmList: data.data },
            });

            NotificationManager.success(instanceName + 'Virtual Machine Has Been Deactivated', "Success",3000);

            return Promise.resolve();
        },
        (error) => {
            console.log(error)
            const message =
                (error.response &&
                    error.response.data) ||
                error.toString();

            NotificationManager.error(message, 'Error!', 5000);

            return Promise.reject();
        }

    );
};
