import React, { useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import {activateMachine, deactivateMachine} from '../../actions/cloud';

const VmCard = ({ singleItem }) => {

  const dispatch = useDispatch();

  const handleMachineStatus = async() => {
     if(singleItem.status==="TERMINATED") {
       return dispatch(activateMachine(singleItem.name, singleItem.zone));
     }
     return dispatch(deactivateMachine(singleItem.name, singleItem.zone));
  }

  return (
    <>
      <div className={singleItem.status === "TERMINATED" ? "card text-bg-danger mb-3" : "card text-bg-success mb-3"}>
        <div className="card-header"> <h5 className="card-title">Instance Name: {singleItem.name}</h5> </div>
        <div className="card-body">
          <h5>Status: {singleItem.status} <p></p></h5>
          <h5>Zone: {singleItem.zone}</h5>
          <p className="card-text">Last Started: {singleItem.lastStartTimestamp}  -  Last Stopped: {singleItem.lastStopTimestamp}</p>
          <p className="card-text"></p>
        </div>
        <div className="card-footer">
          <div className="d-grid gap-2">
            <button
              className={singleItem.status === "TERMINATED" ? "btn btn-success" : "btn btn-danger"}
              type="button"
              onClick={handleMachineStatus}
            >
              {singleItem.status === "TERMINATED" ? "Activate" : "Stop"}
            </button>
          </div>
        </div>
      </div>
    </>
  )
}
export default VmCard;
