import {
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    LOGOUT,
    SET_MESSAGE,
} from "./types";

import AuthService from "../services/auth.service";
import { NotificationManager} from 'react-notifications';


export const login = (email, password) => (dispatch) => {
    return AuthService.login(email, password).then(
        (data) => {
            dispatch({
                type: LOGIN_SUCCESS,
                payload: { user: data },
            });

            NotificationManager.success('Login Successfull',"Info",3000);

            return Promise.resolve();
        },
        (error) => {

            const message =
                (error.response &&
                    error.response.data) ||
                error.toString();

            dispatch({
                type: LOGIN_FAIL,
            });

            NotificationManager.error(message, 'Error!', 5000);

            return Promise.reject();
        }
    );
};

export const logout = () => (dispatch) => {
    AuthService.logout();

    NotificationManager.warning('Logged out', 'Info', 3000);

    dispatch({
        type: LOGOUT,
    });
};
