import {
    FETCH_LIST,
    FETCH_LIST_FAILED,
    SET_MESSAGE,
} from "./types";

import CloudService from "../services/cloud.service";

export const fetchList = () => (dispatch) => {
    return CloudService.getVmList().then(
        (data) => {
            dispatch({
                type: FETCH_LIST,
                payload: { vmList: data.data },
            });
            NotificationManager.success('Virtual Machine Listed Fetched',"Success");
            return Promise.resolve();
        },
        (error) => {
            console.log(error)
            const message =
                (error.response &&
                    error.response.data) ||
                error.toString();

            dispatch({
                type: SET_MESSAGE,
                payload: message,
            });

            return Promise.reject();
        }
    );
};
